        <div style={{textAlign: "center"}}>
            <a className="waves-effect waves-light btn-large" onClick={()=>this.setState({res: "0"})}>0</a>
            <a className="waves-effect waves-light btn-large" onClick={()=>this.setState({res: "1"})}>1</a>
            <a className="waves-effect waves-light btn-large" onClick={()=>this.setState({res: "2"})}>2</a>
        </div>
        <style jsx>
